function Teachers_Table_insert() {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('Teachers_Table');
	if(!$arrPerm[1]) return false;

	$data = array();
	$data['Email_id'] = $_REQUEST['Email_id'];
		if($data['Email_id'] == empty_lookup_value) { $data['Email_id'] = ''; }
	$data['Ph_number'] = $_REQUEST['Ph_number'];
		if($data['Ph_number'] == empty_lookup_value) { $data['Ph_number'] = ''; }

	// hook: Teachers_Table_before_insert
	if(function_exists('Teachers_Table_before_insert')) {
		$args = array();
		if(!Teachers_Table_before_insert($data, getMemberInfo(), $args)) { return false; }
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Teachers_Table', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	// hook: Teachers_Table_after_insert
	if(function_exists('Teachers_Table_after_insert')) {
		$res = sql("select * from `Teachers_Table` where `Name`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!Teachers_Table_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('Teachers_Table', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) Teachers_Table_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

function Teachers_Table_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = array(); // array of curl handlers for launching insert requests
	$eo = array('silentErrors' => true);
	$uploads_dir = realpath(dirname(__FILE__) . '/../' . $Translation['ImageFolder']);
	$safe_sid = makeSafe($source_id);

	// copy Teachers_Table
	$res = sql("SELECT * FROM `Teachers_Table` WHERE `Ph_number`='{$safe_sid}'", $eo);
	while($row = db_fetch_assoc($res)) {
		$data = array(
			'SelectedID' => $row['Name'],
			'Email_id' => $row['Email_id'],
			'Ph_number' => $destination_id,
		);

		$ch = curl_insert_handler('Teachers_Table', $data);
		if($ch !== false) $requests[] = $ch;
	}

	// launch requests, asynchronously
	curl_batch($requests);
}

