		return $RetMsg;
	}

	sql("delete from `Teachers_Table` where `Name`='$selected_id'", $eo);

	// hook: Teachers_Table_after_delete
	if(function_exists('Teachers_Table_after_delete')) {
		$args=array();
		Teachers_Table_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='Teachers_Table' and pkValue='$selected_id'", $eo);
}

function Teachers_Table_update($selected_id) {
	global $Translation;

	// mm: can member edit record?
	$arrPerm=getTablePermissions('Teachers_Table');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='Teachers_Table' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='Teachers_Table' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3) { // allow update?
		// update allowed, so continue ...
	}else{
		return false;
	}

	$data['Email_id'] = makeSafe($_REQUEST['Email_id']);
		if($data['Email_id'] == empty_lookup_value) { $data['Email_id'] = ''; }
	$data['Ph_number'] = makeSafe($_REQUEST['Ph_number']);
		if($data['Ph_number'] == empty_lookup_value) { $data['Ph_number'] = ''; }
	$data['selectedID'] = makeSafe($selected_id);

	// hook: Teachers_Table_before_update
	if(function_exists('Teachers_Table_before_update')) {
		$args = array();
		if(!Teachers_Table_before_update($data, getMemberInfo(), $args)) { return false; }
	}

	$o = array('silentErrors' => true);
	sql('update `Teachers_Table` set       `Email_id`=' . (($data['Email_id'] !== '' && $data['Email_id'] !== NULL) ? "'{$data['Email_id']}'" : 'NULL') . ', `Ph_number`=' . (($data['Ph_number'] !== '' && $data['Ph_number'] !== NULL) ? "'{$data['Ph_number']}'" : 'NULL') . " where `Name`='".makeSafe($selected_id)."'", $o);
	if($o['error']!='') {
		echo $o['error'];
		echo '<a href="Teachers_Table_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: Teachers_Table_after_update
	if(function_exists('Teachers_Table_after_update')) {
		$res = sql("SELECT * FROM `Teachers_Table` WHERE `Name`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['Name'];
		$args = array();
		if(!Teachers_Table_after_update($data, getMemberInfo(), $args)) { return; }
	}

	// mm: update ownership data
	sql("update `membership_userrecords` set `dateUpdated`='" . time() . "' where `tableName`='Teachers_Table' and `pkValue`='" . makeSafe($selected_id) . "'", $eo);

}

